import { useEffect, useState } from 'react'
import { getData } from '../DAL/api'
import { Multiselect } from 'multiselect-react-dropdown'
import { Row } from 'react-bootstrap'
import * as Yup from 'yup'
const Search = () => {
    const [recipes, setRecipes] = useState([])
    const [ingredients, setIngredients] = useState([])
    const [selectedIngredients, setSelectedIngredients] = useState([])
    const [diets, setDiets] = useState([])
    const [selectedDiets, setSelectedDiets] = useState([])
    const [categories, setCategories] = useState([])
    const [selectedcategories, setSelectedcategories] = useState([])
    const [recipeText, setRecipeText] = useState('')

    useEffect(async () => {
        const getAllRecipes = await getData('recipes')
        const getIngredients = await getData('ingredients')
        const getCategories = await getData('categories')
        const getDiets = await getData('diets')
        setRecipes(getAllRecipes)
        setIngredients(getIngredients)
        setCategories(getCategories)
        setDiets(getDiets)
    }, [])


    return <div>
        <h1>Multi Select</h1>
        <Row>
            {ingredients.length && <Multiselect options={ingredients} displayValue={'name'} selectedValues/>}
            {categories.length && <Multiselect options={categories} displayValue={'name'} />}
            {diets.length && <Multiselect options={diets} displayValue={'name'} />}
        </Row>



    </div>


}
export default Search;
