export default function validateInfo(values) {
  let errors = {};
  errors.username = [];
  errors.email = [];
  errors.gender = [];
  errors.course = [];
  errors.adress = [];
  errors.password = [];
  errors.password2 = [];

  if (!values.username.trim()) {
    errors.username.push("נא להכניס שם משתמש");
  }
  if (values.username.length < 2) {
    errors.username.push("username must be more then 2 characters");
  }

  if (!values.email) {
    errors.email.push("Email required");
  }
  if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(values.email)) {
    errors.email.push("Email address is invalid");
  }

  if (!values.adress) {
    errors.adress.push("address is required");
  }
  if (values.adress.length < 10) {
    errors.adress.push("Address must be more then 9 characters");
  }

  if (!values.gender) {
    errors.gender.push("gender is required");
  }

  if (!values.password) {
    errors.password.push("נא הכנס סיסמא");
  }
  if (values.password != values.password2) {
    console.log(values.password2);
    errors.password2.push("הסיסמאות אינן זהות");
  }

  return errors;
}
