import { useState, useEffect } from "react";
const useForm = (callback, validate) => {
  const [values, setValues] = useState({diet:[],category});
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  values.diet = []
  values.categ0ry = []

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (e.target.name == 'diet') {
      if (values.diet.includes(value)) {
        const newDiet = values.diet.filter(item => item !== value);
        console.log(newDiet);
        const diet = newDiet;
        values.diet = diet
        console.log('valuesss', values);
        const newVal = values
        setValues(newVal)



      } else {
        values.diet.push(value)

        //setValues(...values, )
      }
    } else if (e.target.type == "checkbox" && values[name]) {
      delete values[name];
      setValues({ ...values });
    } else {
      setValues({ ...values, [name]: value });
    }
  };
  console.log('redular->', values);

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors(validate(values));
    setIsSubmitting(true);
  };

  useEffect(() => {
    for (const key in errors) {
      if (!!errors[key].length) return;
    }
    isSubmitting && callback();
  }, [errors]);
  return { handleChange, handleSubmit, values, errors };
};

export default useForm;
