import React, { useEffect, useState } from "react";
import "./registerform.css";
import { Link, useHistory } from "react-router-dom";
import { Form, Col, Button, Row } from "react-bootstrap";
import InputCreator from "./InputCreator";

import { getData, insertDataToDB, appendUser, getSpecificUser } from "../DAL/api";
import * as Yup from 'yup'
import { useFormik } from "formik";
const Cookies = require('js-cookie')
const RegisterForm = ({ recipe, editUser }) => {
  const [editMode, setEditMode] = useState(false);
  const [diets, setDiets] = useState([]);
  const [meesages, setMessages] = useState('')
  const history = useHistory()




  const insertToForm = (user) => {
    console.log('user', user);
    const email = user.email;
    console.log(email);
    formik.values.firstName = user.firstName;
    formik.values.lastName = user.lastName;
    formik.values.email = user.email;
    formik.values.password = user.password;
    formik.values.confirmPassword = user.password;
    const arr = []
    user.favorites.map(item => {
      arr.push(item.DietId)
    })
    formik.values.diet = arr;
    console.log(formik.values);
  }

  useEffect(async () => {
    if (editUser) {
      const id = Cookies.get("user")
      setEditMode(true);
      const getUser = await getSpecificUser(id)

      insertToForm(getUser)

    }

    const fetchDiets = await getData('diets')
    setDiets(fetchDiets);
  }, []);


  const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      diet: [],
      confirmPassword: '',

    },

    validationSchema: Yup.object({
      firstName: Yup.string()
        .max(15, 'Must be 15 characters or less')
        .required('Required'),
      lastName: Yup.string()
        .max(20, 'Must be 20 characters or less')
        .required('Required'),
      email: Yup.string()
        .email('Email is invalid')
        .required('Email is required'),
      password: Yup.string()
        .min(6, 'Password must be at least 6 charaters')
        .required('Password is required'),
      confirmPassword: Yup.string()
        .oneOf([Yup.ref('password'), null], 'Password must match')
        .required('Confirm password is required'),
      diet: Yup.array().required('required')
        .min(2, 'must check 2 types')
    })
    ,

    onSubmit: async (values) => {
      const res = await insertDataToDB('users', formik.values);
      alert(JSON.stringify(values))
      const data = new FormData();
      if (!editUser) {
        const response = await appendUser(values)
      } else {

        // const update = await updateUser(data)
      }

    },

  })




  return (
    <>
      <Form dir="rtl" onSubmit={formik.handleSubmit}>
        <div className="form-inner">
          <Form.Row className="m-auto rtl">
            <h1 className="text-center">Registration page</h1>
          </Form.Row>
          <hr />
          <Form.Group>
            <InputCreator
              title="First name"
              type="text"
              required="true"
              value={formik.values.firstName}
              placeholder="First Name"
              sign={<i class="fas fa-user"></i>}
              onChange={formik.handleChange}
              name="firstName"
            />
            {formik.touched.firstName && formik.errors.firstName ? <p className='text-danger'>{formik.errors.firstName} </p> : ""}

          </Form.Group>
          <Form.Group>
            <InputCreator
              title="Last name"
              type="text"
              required="true"
              value={formik.values.lastName}
              placeholder="last Name"
              sign={<i class="fas fa-user"></i>}
              onChange={formik.handleChange}
              name="lastName"
            />
            {formik.touched.lastName && formik.errors.lastName ? <p className='text-danger'>{formik.errors.lastName} </p> : ""}
          </Form.Group>
          <Form.Group controlId="formGridPassword">
            <InputCreator
              title="Email"
              type="email"
              value={formik.values.email}
              required="true"
              onChange={formik.handleChange}
              placeholder="Email"
              sign={<i class="fas fa-mail-bulk"></i>}
              name="email"
            />
            {formik.touched.email && formik.errors.email ? <p className='text-danger'>{formik.errors.email} </p> : ""}
          </Form.Group>
          <Form.Group>
            <InputCreator
              title="Password"
              type="password"
              required="true"
              value={formik.values.password}
              placeholder="password"
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={formik.handleChange}
              name="password"
              note="Minimum password requirements: at least a digit, 
              lowercase and uppercase letter in English. And no less than 8 characters"
            />
            {formik.touched.password && formik.errors.password ? <p className='text-danger'>{formik.errors.password} </p> : ""}

          </Form.Group>
          <Form.Group>
            <InputCreator
              title="Confirm password"
              type="password"
              required="true"
              value={formik.values.confirmPassword}
              placeholder=""
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={formik.handleChange}
              name="confirmPassword"

              note="The content of the field will be the same as the content typed for the password"
            />
            {formik.touched.confirmPassword && formik.errors.confirmPassword ? <p className='text-danger'>{formik.errors.confirmPassword} </p> : ""}

          </Form.Group>
          <Form.Group>
            <Row>
              <Col>
                <label className='form-label text-left d-block'>Favorite diet</label>
              </Col>
              <Col className="d-flex flex-row flex-wrap col-sm-10">
                {diets.length &&
                  diets.map((diet) => {
                    return (
                      <Form.Check
                        type="checkbox"
                        label={diet.name}
                        name={'diet'}
                        value={diet.id}
                        checked={diet.id in formik.values.diet}
                        onChange={(e) => {
                          let checked = e.target.checked;
                          diets.map(item => {
                            item.select = checked;
                            return item
                          })
                        formik.handleChange();
                        }}
                      />
                    );
                  })}
                {formik.touched.diet && formik.errors.diet ? <p className='text-danger'>{formik.errors.diet} </p> : ""}

              </Col>
            </Row>
          </Form.Group>
          {
            <Button type="submit" id="submit" >
              {editMode ? "update" : "sign-up"}
            </Button>
          }
          <Link to="/login">have an acount?login</Link>
        </div>
      </Form>
    </>
  );
};

export default RegisterForm;
