export default function Search() {
  const [contacts, setContacts] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const API_URL = "https://my.api.mockaroo.com/phonebook.json?key=9ac1c5f0";
    fetch(API_URL).then(Response=>Response.json).then(res=>setContacts(res))
    // ft.get(API_URL).then((res) => {
    //   const contacts = res.data;
    //   setContacts(contacts);
    // });
  }, []);

  const filteredContacts =
    search.length === 0
      ? contacts
      : contacts.filter((contact) =>
          contact.full_name.toLowerCase().includes(search.toLowerCase())
        );

  return (
    <div>
      <h3>CONTACTS LIST</h3>
      <input
        type="text"
        placeholder="Search name"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <List contacts={filteredContacts} />
    </div>
  );
}
