import { useEffect, useState } from "react";
import fetch from "../DAL/api";
import { Modal, ListGroupItem, Button, Image } from "react-bootstrap";
import { Carousel } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./Carousel.css";
import "./ItemDetails.css";
import { getData } from "../DAL/api";
const fetchData = require('../DAL/api')

const axios = require('axios')
function ItemDetails(props) {
  const { match, product, onAdd } = props
  console.log('item details->', props.location.add);
  const [item, setItem] = useState([]);
  const [images, setImages] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [ingredients, setIngredients] = useState([]);


  const [openEditForm, setOpenEditform] = useState(false);

  useEffect(async () => {
    const imagesArray = [];
    const instructionsArray = []
    const ingredientsArray = []
    const item = await getData('recipes')
    const fetchImages = await getData('images')
    const getInstructions = await getData('instructions')
    const getIngredients = await getData('recipe_ingredients')
    const displayItem = item.find((el) => el.recipeId == match.params.id);
    fetchImages.forEach((element) => {
      if (element.recipeId == match.params.id) imagesArray.push(element.imgUrl)
    });
    getInstructions.forEach((element) => {
      if (element.recipeId == item.recipeId) instructionsArray.push(element.instruction)
    });
    getIngredients.forEach((element) => {
      if (element.recipeId == item.recipeId) ingredientsArray.push(element.in)
    });
    setImages(imagesArray);
    setInstructions(instructionsArray)
    console.log(getInstructions);
    setItem(displayItem);
  }, []);
  return (
    <div className="form-inner product">
      <Carousel fade>
        {images.length > 0 &&
          images.map((image, i) => (
            <Carousel.Item key={i}>
              <img className="m-auto" src={image} alt="1" />
              <Carousel.Caption>
                <h3 className="text-center">{item.name}</h3>
                <p>{item.description}</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
      </Carousel>
      <div>
        <ListGroupItem> name: {item.name}</ListGroupItem>
        <ListGroupItem> prep time: {item.prep_time_min} minutes</ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          {" "}
          ingredients: {item.recipeId}
        </ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          {" "}
          instrictions: <ol className='break-word text-left'> {instructions.length > 0 && instructions.map((inst, i) => {

            return <li key={i}>{inst} </li>
          })}
          </ol>
        </ListGroupItem>

        <div class="collapse" id="collapseExample">
          <div class="card card-body">
            1:{item.name}
            2:{item.name}
            3:{item.name}
            4:{item.name}
          </div>
        </div>
      </div>
      <div>
        <Link className="btn btn-primary m-2" to={`/edit/${item.recipeId}`}>
          Edit
        </Link>
        <Link className="btn btn-success" >
          <div>
            <button >Add To Cart</button>
          </div>
        </Link>
      </div>
    </div>
  );
}

export default ItemDetails;
