import { useEffect, useState } from "react";
import {
  Accordion,
  Card,
  Button,
  Form,
  Col,
  Row,
  Table,
} from "react-bootstrap";
import useForm from "../utills/useForm";
import validate from "../utills/validateInfo";
import { getIngredients, getUnits, getDiets, getCategories } from "../DAL/api";
import ErrorMessages from "../utills/ErrorMessages";
import axios from 'axios'

function NewRecipeForm(props) {
  const [ingredients, setIngredients] = useState([]);
  const [units, setUnits] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [guide, setGuide] = useState([]);
  const [diets, setDiets] = useState([]);
  const [categories, setCategories] = useState([]);
  const { handleSubmit, handleChange, values, errors } = useForm(
    submit,
    validate
  );
  const deleteInstruction = (index) => {
    instructions.splice(index, 1);
    setInstructions([...instructions]);
  };

  const deleteGuide = (index) => {
    guide.splice(index, 1);
    setGuide([...guide]);
  };

  function submit(e) {
    props.addUser(values);
  }

  useEffect(async () => {
    const response = await axios.get('http://localhost:3200/ingredients')
    setIngredients(response.data);
    const fetchUnits = await axios.get('http://localhost:3200/ingredients')
    const fetchDiets = await axios.get('http://localhost:3200/ingredients')
     const fetchCategories = await getCategories();
     setUnits(fetchUnits);
    // //setIngredients(fetchIngredients);
    // setDiets(fetchDiets);
    // setCategories(fetchCategories);
  }, []);

  return (
    <Form onSubmit={handleSubmit}>
      <div className="form-inner">
        <h1>Adding new recipe</h1>
        <Row className="container">
          <Col className="col-md-3">
            <Form.Label>name </Form.Label>
            <Form.Control type="text" name="username" onChange={handleChange} />
            {errors.username && <ErrorMessages errors={errors.username} />}
          </Col>
          <Col className="col-md-3 mr-4">
            <Row>
              <Form.Label as="Row" column sm={10}>
                view permition
              </Form.Label>
            </Row>
            <Row sm={10}>
              <Form.Check
                type="radio"
                label="public"
                defaultValue={false}
                name="isPrivate"
                onChange={handleChange}
              />
              <Form.Check
                type="radio"
                value={true}
                label="private"
                name="isPrivate"
                onChange={handleChange}
              />
              {errors.isPrivate && <ErrorMessages errors={errors.isPrivate} />}
            </Row>
          </Col>
          <Col>
            <Form.File
              id="exampleFormControlFile1"
              label="main pic"
              name="image"
              value={values.image}
              onChange={handleChange}
            />
            {errors.image && <ErrorMessages errors={errors.image} />}
          </Col>
        </Row>
        <Row className="container-fluid">
          <Form.Label> source</Form.Label>
          <Form.Control
            type="text"
            placeholder="recipe source"
            name="source"
            value={values.source}
            onChange={handleChange}
          />
          <Form.Label> link to source </Form.Label>
          <Form.Control
            type="text"
            placeholder=" if exists"
            name="sourceUrl"
            onChange={handleChange}
          />
        </Row>

        <Accordion defaultActiveKey="0">
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="3">
                General description
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="3">
              <div>
                <Form.Group
                  className="mb-3 container-fluid"
                  controlId="exampleForm.ControlTextarea1"
                >
                  <Form.Label>
                    The description will appear below the title
                  </Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    name="description"
                    onChange={handleChange}
                  />
                </Form.Group>
                <Row className="container w-75 m-auto">
                  <Col>
                    <Row className="mr-3">diet type:</Row>
                    <Row>
                      {diets.length &&
                        diets.map((diet) => {
                          return (
                            <Form.Check
                              type="checkbox"
                              label={diet.name}
                              name='diet'
                              value={diet.id}
                              onChange={handleChange}
                            />
                          );
                        })}
                      {errors.diet && <ErrorMessages errors={errors.diet} />}
                    </Row>
                  </Col>

                  <Col>
                    <Row className="mr-3">Suitable for category</Row>
                    <Row>
                      {categories.length &&
                        categories.map((category) => {
                          return (
                            <Form.Check
                              type="checkbox"
                              label={category.name}
                              name='category'
                              value={category.id}
                              onChange={handleChange}
                            />
                          );
                        })}
                      {errors.category && <ErrorMessages errors={errors.category} />}
                    </Row>

                  </Col>
                </Row>
              </div>
            </Accordion.Collapse>
          </Card>
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="0">
                Ingredients
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="0">
              <Row>
                <Row>
                  <Table
                    triped
                    bordered
                    hover
                    size="sm"
                    className="m-auto text-center "
                  >
                    <tbody>
                      {instructions.length > 0 &&
                        instructions.map((item, i) => (
                          <tr key={i}>
                            <td>
                              {(item, item)}
                              <Button
                                className="btn-danger px-1 my-1 "
                                onClick={() => deleteInstruction(i)}
                              >
                                <i class="fas fa-trash text-sm"></i>
                              </Button>
                            </td>
                            <td>{item}</td>
                            <td>{item}</td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                </Row>
                <Row className="p-5">
                  <Col className="col-md-1">
                    <Form.Label>quantity</Form.Label>
                    <Form.Control type="number" min={1} />
                  </Col>
                  <Col className="col-md-2">
                    <Form.Label>measurement unit</Form.Label>
                    <select onChange={handleChange}>
                      {units.map((unit, i) => {
                        return (
                          <option key={i} value={unit.id}>
                            {unit.name}
                          </option>
                        );
                      })}
                    </select>
                  </Col>

                  <Col>
                    <Form.Label>Ingredients</Form.Label>
                    <select onChange={handleChange}>
                      {ingredients.map((ingredient, i) => {
                        return (
                          <option key={i} value={ingredient.id}>
                            {ingredient.name}
                          </option>
                        );
                      })}
                    </select>
                  </Col>
                  <Col className="col-md-2">
                    <Form.Label>notes</Form.Label>
                    <Form.Control as="input" />
                  </Col>
                  <Col className="flex-grow-0">
                    <Button
                      className="mb-2"
                      onClick={() => setInstructions([...instructions, "get"])}
                    >
                      Save
                    </Button>
                    <Button onClick={() => setInstructions([])}>Clear</Button>
                  </Col>
                </Row>
              </Row>
            </Accordion.Collapse>
          </Card>
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="1">
                Preparation steps
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="1">
              <Row className="container-fluid">
                <Row className="mr-2">
                  <Table triped bordered size="sm">
                    <tbody>
                      {guide.length > 0 &&
                        guide.map((item, i) => (
                          <tr key={i}>
                            <td>
                              {(item, item)}
                              <Button
                                className="btn-danger px-1 my-1 "
                                onClick={() => deleteGuide(i)}
                              >
                                <i class="fas fa-trash text-sm"></i>
                              </Button>
                            </td>
                            <td>{item}</td>
                            <td>{item}</td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                </Row>
                <Row className="d-flex p-5">
                  <Col className="col-md-2">
                    <Form.Label>#</Form.Label>
                    <Form.Control as="input" />
                  </Col>
                  <Col className="col-md-4">
                    <Form.Label>instruction</Form.Label>
                    <Form.Control as="input" />
                  </Col>
                  <Col className="col-md-2">
                    <Button
                      className="m-2 flex-grow-0"
                      onClick={() => setGuide([...guide, "get"])}
                    >
                      Save
                    </Button>
                  </Col>
                </Row>
              </Row>
            </Accordion.Collapse>
          </Card>
        </Accordion>
        <Row className="text-left">
          <Button type="submit" className="m-3">
            Add Recipe
          </Button>
        </Row>
      </div>
    </Form>
  );
}
export default NewRecipeForm;
