const getLocalItems = () => {
    const list = localStorage.getItem("list");
    if (list) {
        return JSON.parse(localStorage.getItem("list"));
    } else {
        return [];
    }
};

function LocalCart() {
    const [list, setList] = useState([])
    useEffect(() => {
        const fetchList = getLocalItems
        setList()
    }, [])
