import { useState } from "react";
import { Row } from "react-bootstrap";
import validateInfo from "./validateInfo";
import { login } from "./api.js";
function Login() {
  const [password, setPasseord] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  function changePassword(e) {
    console.log("password");
    setPasseord(e.target.value);
  }
  
  function changeEmail(e) {
    console.log("email");
    setEmail(e.target.value);
  }
  function onSubmit(e) {
    e.preventDefault();
    if (login(email, password)) alert("Welcome");
    else setMessage("something worng,please check agin the details");
  }
  return (
    <div className="container w-25">
      <h1>כניסה</h1>
      <label htmlFor="email">אימייל</label>
      <input
        id="email"
        name="email"
        type="text"
        className="form-control"
        onChange={(e) => {
          changeEmail(e);
        }}
        defaultValue={email}
      />
      <label htmlFor="email">סיסמא</label>
      <input
        id="password"
        name="password"
        type="text"
        className="form-control"
        onChange={(e) => {
          changePassword(e);
        }}
        aria-describedby="emailHelp"
        defaultValue={password}
      />
      <Row>
        <button
          className="btn btn-primary my-2 text-right font-weight-bold "
          type="submit"
          onClick={onSubmit}
        >
          כניסה
        </button>
        <button className="btn btn-primary my-2 mx-3">ביטול</button>
      </Row>
      <Row>
        <small>
          <a href="/sign-up">אין משתמש?לחץ להרשמה </a>
        </small>
      </Row>
    </div>
  );
}
export default Login;
