function Profile() {

    const [userRecipes, setUserRecipes] = useState([])
    const [images, setImages] = useState([])
    useEffect(async () => {
        const recipes = await axios.get('http://localhost:3200/recipes')
        const newImages = await axios.get('http://localhost:3200/images')
        console.log();
        setImages(newImages.data)
        const userId = localStorage.getItem('name')
        console.log(recipes);
        const recipeByUser = recipes.data.filter(item => item.userId == userId)
        setUserRecipes(recipeByUser);
        console.log(userRecipes);
    }, [])


    return (
        <ShowAllRecipes recipes={userRecipes} images={images} />
    )
}
export default Profile
