import React, { useState } from "react";
import demoData from "./demoData";
import CardItem from "../../CardItem";
function App1() {
  const dataList = demoData();
  const [searchText, setSearchText] = useState("");
  const [data, setData] = useState(dataList);

  // exclude column list from filter
  const excludeColumns = ["id", "color"];

  // handle change event of search input
  const handleChange = (value) => {
    setSearchText(value);
    filterData(value);
  };

  // filter records by search text
  const filterData = (value) => {
    const lowercasedValue = value.toLowerCase().trim();
    if (lowercasedValue === "") setData(dataList);
    else {
      const filteredData = dataList.filter((item) => {
        return Object.keys(item).some((key) =>
          excludeColumns.includes(key)
            ? false
            : item[key].toString().toLowerCase().includes(lowercasedValue)
        );
      });
      setData(filteredData);
    }
  };

  return (
    <div className="App">
      <br />
      <br />
      Search:{" "}
      <input
        style={{ marginLeft: 5, 'text-align':'center' }}
        type="text"
        placeholder="Type to search..."
        value={searchText}
        onChange={(e) => handleChange(e.target.value)}
      />
      <div className="box-container d-flex flex-wrap p-fixed text-center">
        {searchText.length > 0 &&
          data.map((d, i) => {
            return (
              <CardItem src='https://images.ctfassets.net/hrltx12pl8hq/7yQR5uJhwEkRfjwMFJ7bUK/dc52a0913e8ff8b5c276177890eb0129/offset_comp_772626-opt.jpg?fit=fill&w=800&h=300'/>
              // <div
              //   key={i}
              //   className="box m-2"
              //   style={{ backgroundColor: d.color, "z-index": "2" }}
              // >
              //   <b>Name: </b>
              //   {d.name}
              //   <br />
              //   <b>Year: </b>
              //   {d.year}
              //   <br />
              // </div>
            );
          })}
        <div className="clearboth"></div>
        {data.length === 0 && <span>No records found to display!</span>}
      </div>
    </div>
  );
}

export default App1;
