import { useState, useEffect } from "react";
import ShowAllRecipes from "../sections/ShowAllRecipes";





function Profile() {

    const [userRecipes, setUserRecipes] = useState([])
    useEffect(async () => {
        const recipes = await axios.get('http://localhost:3200/recipes')
        const recipeByUser = recipes.data.filter(item => item.userId === localStorage.getItem('name'))
        setUserRecipes(recipeByUser);

    })


    return (
        <ShowAllRecipes recipes={userRecipes} />
    )
}
export default Profile
